import React, { useState, useCallback } from "react";
import { Button, Form, Input } from "antd";
import Link from "next/link";
import styled from "styled-components";

const LoginForm = () => {
  // id, password의 상태를 초기화
  // setId, setPassword로 상태를 업데이트
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  // id, password의 필드값이 변경 즉, 입력될때 호출되는 콜백함수이다.
  // "userCallback" 훅을 사용하여 생성됨
  // 해당 입력 필드의 값(id, pawwsord)을 상태로 업데이트한다.
  const onChangeId = useCallback((e) => {
    setId(e.target.value);
  }, []);
  const onChangePassword = useCallback((e) => {
    setPassword(e.target.value);
  }, []);

  return (
    <Form>
      <div>
        <label htmlFor="user-id">아이디</label>
        <br />
        <input name="user-id" value={id} onChange={onChangeId} required />
      </div>
      <div>
        <label htmlFor="user-password">비밀번호</label>
        <br />
        <input
          name="user-password"
          type="password"
          value={password}
          onChange={onChangePassword}
          required
        />
      </div>
      <div style={{ marginTop: 10 }}>
        {/*//이렇게 객체로 생성하여 div 태그에 스타일을 주면 안되는 이유 :
        리엑트의 버추얼 DOM은 컴포넌트의 상태나 프로퍼티가 변경되면 해당
        컴포넌트의 가상DOM을 생성하고, 이전 가상 DOM과 비교하여 변경된 부분만
        찾아내어 실제 DOM에 반영하는 방식으로 동작한다. 아때, 객체로 스타일을
        정의하면 매번 렌더링 시에 새로운 객체를 생성하게되고 객체는 참조형이기
        때문에 값이 직접 복사가 아닌 값이 저장된 메모리 주소를 복사한다 따라서
        저장된 값은 같지만 렌더링 전에 정의된 객체와 렌더링 후에 생성된 객체는
        다른 객체이고 가상 DOM이 비교 후 변경된 부분이라 판단되어 실제 DOM에
        반영을 하게 된다. 이러한 이유로 불필요하게 자주 리렌더링이 되거나 스타일
        객체가 크고 복잡한 경우 성능 저하 문제가 생긴다 */}
        <Button type="primary" htmlType="submit" loading={false}>
          로그인
        </Button>
        <Link href="/signup">
          <a>
            <Button>회원가입</Button>
          </a>
        </Link>
      </div>
    </Form>
  );
};

export default LoginForm;
